export const fetchPhones = async () => {
  var phones = await fetch('http://www.mocky.io/v2/5b0828ae3200008d007001e7')
    .then(response => response.json())
    .then(data => data.phones)
  return phones
}

export const loadMorePhones = async ({offset}) => { //use offset later 
  return phones
  //return Promise.reject('error')
}

export const fetchPhoneById = async (id) => R.find(R.propEq('id', id), phones)

export const fetchCategories = async () => {
  return categories
}