import R from 'ramda'

const getPhoneById = (state, id) => R.prop(id, state.phones) // s → {s: a} → a | Undefined

export const getPhones = state => {
    //return state.phonesPage.ids
    // console.log("state from getPhones", state)
    // console.log("getById", getPhoneById(state, "2"))
    return R.isEmpty(state.phones) ? [] : R.map(id => getPhoneById(state, id), state.phonesPage.ids)
}