import R from 'ramda'

import {
  FETCH_PHONES_FULFILLED,
  LOAD_MORE_PHONES_FULFILLED,
  FETCH_PHONE_BY_ID_FULFILLED
} from 'constants'

const initialState = {}

export default (state = initialState, { type, payload }) => {
  switch (type) {
    case FETCH_PHONES_FULFILLED:
      const newValues = R.indexBy(R.prop('id'), payload) // [{k: v}] â†’ {k: {k: v}}
      return R.merge(state, newValues)
    case LOAD_MORE_PHONES_FULFILLED:
      const moreValues = R.indexBy(R.prop('id'), payload)
      return R.merge(state, moreValues)
    case FETCH_PHONE_BY_ID_FULFILLED:
      return R.assoc(payload.id, payload, state)
    default:
      return state
  }
}