import R from 'ramda'

import {
    FETCH_PHONES_FULFILLED
} from 'constants'

const initialState = {}

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_PHONES_FULFILLED:
            const newValues = R.indexBy(R.prop('id'), payload) // [{k: v}] â†’ {k: {k: v}}
            return R.merge(state, newValues)
        default:
            return state
    }
}