import R from 'ramda'

import {
  FETCH_PHONES_FULFILLED,
  LOAD_MORE_PHONES_FULFILLED
} from 'constants'

const initialState = { ids: [] }

export default (state = initialState, { type, payload }) => {
  switch (type) {
    case FETCH_PHONES_FULFILLED:
      return R.merge(state, {
        ids: R.pluck('id', payload)
      })
    case LOAD_MORE_PHONES_FULFILLED:
      return R.merge(state, {
        ids: R.concat(state.ids, R.pluck('id', payload))
      })
    default:
      return state
  }
}